// Handler returns an http.Handler for use with net/http or testing.
// This provides a standard http.Handler interface without requiring an Iris application.
func Handler(svc ServiceInterface, opts ...RouterOption) http.Handler {
	cfg := &routerConfig{}
	for _, opt := range opts {
		opt(cfg)
	}

	adapter := NewHTTPAdapter(svc, cfg.errHandler)
	mux := http.NewServeMux()
	mux.HandleFunc("GET /health", adapter.HealthCheck)
	mux.HandleFunc("GET /users", adapter.ListUsers)
	mux.HandleFunc("POST /users", adapter.CreateUser)
	mux.HandleFunc("POST /users/import", adapter.ImportUsers)
	mux.HandleFunc("GET /users/{id}", adapter.GetUser)
	mux.HandleFunc("DELETE /users/{id}", adapter.DeleteUser)
	mux.HandleFunc("GET /users/{id}/avatar", adapter.GetUserAvatar)
	mux.HandleFunc("PUT /users/{id}/avatar", adapter.UploadUserAvatar)
	mux.HandleFunc("POST /contact", adapter.SubmitContactForm)
	mux.HandleFunc("POST /notes", adapter.CreateNote)
	mux.HandleFunc("POST /xml-data", adapter.ProcessXMLData)
	mux.HandleFunc("GET /export", adapter.ExportData)
	mux.HandleFunc("POST /oauth/token", adapter.GetOAuthToken)
	mux.HandleFunc("GET /items/{type}", adapter.GetItemsByType)
	mux.HandleFunc("GET /search", adapter.Search)
	mux.HandleFunc("GET /status", adapter.GetStatus)
	mux.HandleFunc("POST /images", adapter.UploadImage)
	mux.HandleFunc("GET /products", adapter.ListProducts)
	mux.HandleFunc("GET /categories/{categoryId}", adapter.GetCategory)
	mux.HandleFunc("GET /items/{type}/{rating}", adapter.GetItemsByStatus)
	mux.HandleFunc("GET /users/{id}/posts/{postId}", adapter.GetUserPost)
	mux.HandleFunc("POST /orders", adapter.CreateOrder)
	mux.HandleFunc("POST /companies", adapter.CreateCompany)
	return mux
}

